function createSettingsArray(lang) {
    var i = 0;
    var regions = [];
    for (var key in lang_arrays[lang]) {

        var cities = lang_arrays[lang][key].map(function(el){
           el = el.trim().replace(' ','_');
            return {
               title: el.toUpperCase(),
               key: el.toLowerCase()
           }
        });
        key = key.trim().replace(' ','_');
        regions.push({
            title: key.toUpperCase(),
            key: key.toLowerCase(),
            cities: cities
        });
        i++
    }
    return regions;
}

function landing(lang) {
    var array = [];
    for(var key in lang_arrays[lang]) {
        array.push({
            key: key,
            value: lang_arrays[lang][key]
        })
    }
    return array;
}

