function getLandingArray(array) {
    var landing_array = {};
    obj.ru.forEach(function (item, i) {
        var key = obj.en[i].key.trim().replace(' ','_').toUpperCase();

        landing_array[key] = {
            ru: obj.ru[i].key.trim().toLowerCase(),
            en: obj.en[i].key.trim().toLowerCase(),
            ua: obj.ua[i].key.trim().toLowerCase()
        };

        obj.ru[i].value.forEach(function (item_, j) {
            var key = obj.en[i].value[j].trim().replace(' ','_').toUpperCase();

            landing_array[key] = {
                ru: obj.ru[i].value[j].trim().toLowerCase(),
                en: obj.en[i].value[j].trim().toLowerCase(),
                ua: obj.ua[i].value[j].trim().toLowerCase()
            };
        });



    });
    return landing_array;
}


